    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const elements = {
                charName: document.getElementById('char-name'),
                favorLabel: document.getElementById('favor-label'),
                favorBar: document.getElementById('favor-bar'),
                moodText: document.getElementById('mood-text'),
                lustLabel: document.getElementById('lust-label'),
                lustBar: document.getElementById('lust-bar'),
                secretText: document.getElementById('secret-text'),
                bodyText: document.getElementById('body-text'),
                memoList: document.getElementById('memo-list'),
                doodleArt: document.getElementById('doodle-art'),
                doodleCaption: document.getElementById('doodle-caption'),
                displayContent: document.getElementById('display-content'),
            };

            const rawData = document.getElementById('raw-data').innerHTML;
            let characters = [];
            let currentCharIndex = 0;

            function parseRawData() {
                const charRegex = /<char(\d+)>([\s\S]*?)<\/char\1>/g;
                let match;
                while ((match = charRegex.exec(rawData)) !== null) {
                    const content = match[2];
                    characters.push({
                        name: content.match(/角色名称：(.*?)\n/)?.[1].trim() || 'N/A',
                        favor: content.match(/好感指数：(.*?)\n/)?.[1].trim() || '0',
                        mood: content.match(/情绪气泡：(.*?)\n/)?.[1].trim().replace(/；/g, ' / '),
                        secret: content.match(/隐秘心声：(.*?)\n/)?.[1].trim() || '',
                        lust: content.match(/性欲指数：(.*?)\n/)?.[1].trim() || '0',
                        body: content.match(/器官状态：([\s\S]*?)事务备忘：/)?.[1].trim() || '',
                        memo: content.match(/事务备忘：([\s\S]*?)随性涂鸦：/)?.[1].trim() || '',
                        doodle: content.match(/随性涂鸦：\n([\s\S]*?)涂鸦解析：/)?.[1] || '',
                        doodleCaption: content.match(/涂鸦解析：(.*?)\n/)?.[1].trim() || ''
                    });
                }
            }

            function renderCharacter(index) {
                if (!characters[index]) return;
                const char = characters[index];

                elements.charName.textContent = char.name;
                elements.favorLabel.textContent = `FAVOR [${char.favor}]`;
                elements.moodText.innerHTML = `<strong>MOOD:</strong> ${char.mood}`;
                elements.lustLabel.textContent = `LUST [${char.lust}]`;
                elements.secretText.textContent = char.secret;

                // 【关键改动】使用 innerHTML 并只转义 < 符号
                elements.doodleArt.innerHTML = char.doodle.replace(/</g, '&lt;');

                elements.doodleCaption.textContent = `"${char.doodleCaption}"`;

                const bodyLines = char.body.split('\n').filter(line => line.trim() !== '');
                const formattedBody = bodyLines.map(line => {
                    const processedLine = line.replace(/【.*?】/g, (match) => {
                        return `<span class="organ-tag">${match}</span>`;
                    });
                    return `<p>${processedLine}</p>`;
                }).join('');
                elements.bodyText.innerHTML = formattedBody;

                const memoLines = char.memo.split('\n').filter(line => line.trim() !== '');
                const formattedMemo = memoLines.map(line => `<li>${line}</li>`).join('');
                elements.memoList.innerHTML = formattedMemo;

                setTimeout(() => {
                    const favorValue = parseInt(char.favor, 10);
                    const favorPercentage = (favorValue + 100) / 2;
                    if (elements.favorBar) elements.favorBar.style.width = `${favorPercentage}%`;

                    const lustValue = parseInt(char.lust, 10);
                    const lustPercentage = lustValue;
                    if (elements.lustBar) elements.lustBar.style.width = `${lustPercentage}%`;
                }, 50);
            }

            document.getElementById('prev-char').addEventListener('click', () => {
                if (characters.length > 1) {
                    currentCharIndex = (currentCharIndex - 1 + characters.length) % characters.length;
                    renderCharacter(currentCharIndex);
                }
            });

            document.getElementById('next-char').addEventListener('click', () => {
                if (characters.length > 1) {
                    currentCharIndex = (currentCharIndex + 1) % characters.length;
                    renderCharacter(currentCharIndex);
                }
            });

            parseRawData();
            if (characters.length > 0) {
                renderCharacter(currentCharIndex);
            } else {
                elements.displayContent.innerHTML = "<p>未能加载角色数据...</p>";
            }
        });
    </script>
</body>
</html>
