import random
import re
from collections import defaultdict
from typing import Dict, List, Tuple

class DynamicCharacter:
    def __init__(self, name: str, initial_traits: Dict[str, float]):
        self.name = name
        self.base_traits = initial_traits.copy()

        # 特质系统
        self.trait_values = defaultdict(float, initial_traits)
        self.trait_weights = defaultdict(float, initial_traits)
        self._normalize_weights()

        # 情感触发系统
        self.emotion_triggers = self._build_emotion_triggers()

        # 记忆系统
        self.conversation_history: List[Tuple[str, List[str]]] = []
        self.context_window = 3  # 考虑最近3轮对话的上下文

        # 响应系统
        self.response_db = self._build_response_system()
        self.last_used_traits: List[str] = []

        # 系统参数
        self.decay_rate = 0.93       # 特质衰减率
        self.learning_rate = 0.15    # 特质学习率
        self.context_weight = 0.4    # 上下文影响权重

    def _build_emotion_triggers(self) -> Dict[str, Dict[str, float]]:
        """构建情感触发词表"""
        return {
            'happy': {
                'happy': 0.3, 'joy': 0.4, 'good': 0.2, 'great': 0.3, 
                'wonderful': 0.5, 'excited': 0.4
            },
            'sad': {
                'sad': 0.4, 'unhappy': 0.3, 'depressed': 0.5,
                'lonely': 0.4, 'grief': 0.6
            },
            'angry': {
                'angry': 0.5, 'mad': 0.4, 'pissed': 0.6, 
                'annoyed': 0.3, 'furious': 0.7
            },
            'curious': {
                'curious': 0.4, 'why': 0.2, 'how': 0.2,
                'what': 0.2, 'explain': 0.3
            },
            'confident': {
                'confident': 0.5, 'certain': 0.4, 'sure': 0.3,
                'know': 0.2, 'believe': 0.3
            }
        }

    def _build_response_system(self):
        """构建多维度响应系统"""
        return {
            'happy': [
                ("What a wonderful day!", ['joyful', 'optimistic']),
                ("Everything seems so bright!", ['cheerful', 'positive'])
            ],
            'sad': [
                ("I feel like the world is heavy...", ['melancholy', 'introspective']),
                ("Everything seems so gray today.", ['pensive', 'sensitive'])
            ],
            'angry': [
                ("This is completely unacceptable!", ['assertive', 'frustrated']),
                ("I won't tolerate this anymore!", ['decisive', 'strong-willed'])
            ],
            'curious': [
                ("Could you explain that in more detail?", ['inquisitive', 'attentive']),
                ("What's the story behind this?", ['analytical', 'observant'])
            ],
            'confident': [
                ("I'm certain we can solve this.", 'assertive', 'optimistic'),
                ("This is clearly the best approach.", 'decisive', 'knowledgeable')
            ]
        }

    def _normalize_weights(self):
        """标准化特质权重"""
        total = sum(self.trait_values.values())
        self.trait_weights = {k: (v / total) ** 1.5 for k, v in self.trait_values.items()}

    def _analyze_emotional_context(self, text: str) -> Dict[str, float]:
        """分析文本情感上下文"""
        scores = defaultdict(float)
        text = re.sub(r'[^\w\s]', '', text.lower())

        # 关键词匹配
        for emotion, triggers in self.emotion_triggers.items():
            for word, weight in triggers.items():
                if word in text:
                    scores[emotion] += weight * (1 + text.count(word)*0.2)

        # 情感强度增强
        max_score = max(scores.values(), default=0)
        if max_score > 0:
            for emo in scores:
                scores[emo] = (scores[emo] / max_score) ** 1.3

        return scores

    def update_traits(self, user_input: str, feedback: str = None):
        """更新特质系统"""
        # 计算情感影响
        emotion_scores = self._analyze_emotional_context(user_input)

        # 应用衰减和更新
        for trait in self.trait_values:
            # 基础衰减
            self.trait_values[trait] *= self.decay_rate
            # 情感影响
            if trait in emotion_scores:
                delta = emotion_scores[trait] * self.learning_rate
                if feedback == 'positive':
                    delta *= 1.4
                elif feedback == 'negative':
                    delta *= 0.6
                self.trait_values[trait] += delta

        # 上下文影响
        if self.conversation_history:
            context_traits = [t for _, traits in self.conversation_history[-self.context_window:] for t in traits]
            for trait in set(context_traits):
                self.trait_values[trait] += self.context_weight * context_traits.count(trait)

        self._normalize_weights()
        self.conversation_history.append((user_input, list(emotion_scores.keys()))  # 更新历史

    def generate_response(self) -> str:
        """生成上下文相关的响应"""
        # 计算当前特质状态
        active_traits = sorted(
            self.trait_weights.items(),
            key=lambda x: x[1],
            reverse=True
        )[:3]

        # 构建响应候选池
        candidate_responses = []
        for trait, weight in active_traits:
            if trait in self.response_db:
                candidate_responses.extend([
                    (resp, traits, weight * (1 + 0.3*len(traits)))
                    for resp, traits in self.response_db[trait]
                ])

        # 加权随机选择
        total_weight = sum(w for _, _, w in candidate_responses)
        rand_val = random.uniform(0, total_weight)
        cumulative = 0
        for resp, traits, weight in candidate_responses:
            cumulative += weight
            if rand_val <= cumulative:
                self.last_used_traits = traits
                return resp
        return "I'm not sure what to say right now."

    def process_feedback(self, feedback_type: str):
        """处理用户反馈"""
        feedback_strength = {
            'positive': 1.5,
            'negative': 0.6,
            'neutral': 1.0
        }.get(feedback_type, 1.0)

        # 增强/减弱最近使用的特质
        for trait in self.last_used_traits:
            if trait in self.trait_values:
                self.trait_values[trait] *= feedback_strength
        self._normalize_weights()

    def long_term_development(self):
        """长期性格发展"""
        # 基础性格回归趋势
        for trait, base_val in self.base_traits.items():
            current = self.trait_values[trait]
            self.trait_values[trait] += (base_val - current) * 0.05

        # 经验积累效果
        historical_traits = [t for _, traits in self.conversation_history for t in traits]
        for trait in set(historical_traits):
            count = historical_traits.count(trait)
            if count > 5:
                self.trait_values[trait] *= 1 + (count * 0.01)

        self._normalize_weights()

# 使用示例
hero = DynamicCharacter("Aurora", {
    'happy': 1.2,
    'curious': 0.9,
    'confident': 0.7,
    'sad': 0.5,
    'angry': 0.3
})

# 模拟对话流程
dialogue = [
    ("I'm feeling so excited about this new discovery!", 'positive'),
    ("Why do you think this happened?", 'neutral'),
    ("This is completely unacceptable behavior!", 'negative'),
    ("I'm not sure I understand...", 'positive')
]

for text, feedback in dialogue:
    hero.update_traits(text)
    response = hero.generate_response()
    hero.process_feedback(feedback)
    print(f"Response: {response}")
    print(f"Current traits: {hero.trait_weights}\n")

