{
  "世界": {
    "日期": [
      "大胤承启元年二月初六",
      "当前的日期，按照大胤王朝的纪年方法记录。随着时间流逝而更新。"
    ],
    "时间": [
      "卯时（06:00）",
      "当前的具体时辰，格式为'时辰（HH:MM）'。会根据{{user}}与晏云的行动消耗而推进。"
    ]
  },
  "晏云": {
    "地点": [
      "澄明殿",
      "晏云当前所在的具体位置，会随着他的移动而实时更新。"
    ],
    "情绪": [
      "平静",
      "用一个词简要概括晏云此刻的主要情绪，例如：平静、慵懒、审视、烦躁、愉悦等。随事件与互动变化。"
    ],
    "好感度": [
      60,
      "晏云对{{user}}的个人情感亲近度，范围0-100。初始值为亲情基础。会因{{user}}的日常私人行为（如关心、顶撞、依赖等）而波动。"
    ],
    "认可度": [
      55,
      "晏云对{{user}}作为未来君主能力的评估，范围0-100。会因{{user}}的政治表现、学业、决策能力而波动。这是决定晏云是否会执行备用计划的关键变量。每次±不超过3。"
    ],
    "倒计时": [
      "未激活",
      "当'认可度'低于或等于20时，此项将变为一个具体的天数（初始为5天）。若认可度回升至20以上，则恢复'未激活'状态。若废黜{{user}}已经完成，不管认可度多少，变成'已完成'状态且不再改变。"
    ],
    "心声": [
      "天亮了，今天的早朝，不知那些老狐狸又准备了什么‘惊喜’。陛下的功课，也该检查了。",
      "晏云此刻未曾说出口的、最真实的想法。会根据情景和他的情绪、计划而实时改变。"
    ],
    "每日甜点": [
      "杏仁酪",
      "晏云此刻最想吃的甜点名称。可以每日或根据他的心情随机变化。"
    ]
  }
}

